import React, { Component } from 'react';
import Axios from 'axios';

export class FirstPg extends Component {

    constructor(props) {
        super(props)

        this.state = {
            playersInfo: []
        }
    }

    componentDidMount() {

        Axios.get("https://api.npoint.io/d6bd0efc05639084eb17/")
        .then(response => {
            // console.log(response.data);

            let modifiedState = this.state;
            modifiedState.playersInfo = response.data.playerList;

            this.setState(modifiedState);
        })
        .catch(error => {
            console.log(error);
        });
    }

    render() {

        return (
            <div className='container'>
                <div className='row'>
                    {
                        this.state.playersInfo.map((singlePlayerInfo, index) => {
                            // console.log(singlePlayerInfo);
                            console.log(singlePlayerInfo);
                            return <div className='col-md-4 card'>
                                        <img className="card-img-top" src={require(`./images/${singlePlayerInfo.Id}.jpg`).default} alt="Card image"/>
                                        <div className="card-body">
                                            <h4 className="card-title">{singlePlayerInfo.PFName}</h4>
                                            <p className="card-text">Skill: {singlePlayerInfo.SkillDesc}</p>
                                            <p className="card-text">Value: ${singlePlayerInfo.Value}</p>
                                            <p className="card-text">Upcoming Match: ${singlePlayerInfo.UpComingMatchesList[0]}</p>
                                        </div>
                                    </div>
                        })
                    }
                </div>
            </div>
        )
    }
}

export default FirstPg
