import React, { Component } from 'react';
import Axios from 'axios';

export class FirstPg extends Component {

    constructor(props) {
        super(props)

        this.state = {
            playersInfo: [],
            valueOfPlayer: []
        }
    }

    componentDidMount() {

        Axios.get("https://api.npoint.io/d6bd0efc05639084eb17/")
        .then(response => {
            // console.log(response.data);

            let modifiedState = this.state;
            modifiedState.playersInfo = response.data.playerList;

            for(let i =0; i < response.data.playerList.length; i++) {
                modifiedState.valueOfPlayer.push(response.data.playerList[i]['Value']);
            }

            // console.log(modifiedState.valueOfPlayer);

            this.setState(modifiedState);
        })
        .catch(error => {
            console.log(error);
        });
    }

    getDisplayDate = (date) => {
        console.log(`Date - [${date}]`);

        console.log(Date(date).toUTC());

        // console.log(date.);
    }

    render() {

        return (
            <div className='container-fluid'>
                <div className='row' style={{display: 'flex', justifyContent: 'space-evenly'}}>
                    {
                        this.state.playersInfo.map((singlePlayerInfo, index) => {
                            return <div className='card' style={{marginTop: '2rem'}}>
                                        <img className="card-img-top" src={require(`./images/${singlePlayerInfo.Id}.jpg`).default} alt="Card image"/>
                                        <div className="card-body">
                                            <h4 className="card-title">{singlePlayerInfo['PFName']}</h4>
                                            <p className="card-text">Skill: {singlePlayerInfo['SkillDesc']}</p>
                                            <p className="card-text">Value: ${singlePlayerInfo['Value']}</p>
                                            <p className="card-text">Upcoming Match: {singlePlayerInfo['UpComingMatchesList'][0]['CCode']} vs. {singlePlayerInfo['UpComingMatchesList'][0]['VsCCode']}</p>
                                            <p className="card-text">Next Match Time: {singlePlayerInfo['UpComingMatchesList'][0]['MDate'].replaceAll('/', '-')}</p>                                            
                                        </div>
                                    </div>
                        })
                    }
                </div>
            </div>
        )
    }
}

export default FirstPg
